import React, { useState, useEffect } from 'react';
import { toast } from 'react-hot-toast';
import { useAuth } from '../context/AuthContext';

const FileExplorer = ({ files, onFileSelect, onFileCreate, onFileDelete, onRefresh, selectedFileId }) => {
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [newFileName, setNewFileName] = useState('');
  const [newFileContent, setNewFileContent] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { user } = useAuth();

  const handleCreateFile = async (e) => {
    e.preventDefault();

    if (!newFileName.trim()) {
      toast.error('Please enter a file name');
      return;
    }

    setIsLoading(true);
    try {
      await onFileCreate({
        name: newFileName.trim(),
        content: newFileContent
      });

      setNewFileName('');
      setNewFileContent('');
      setIsCreateModalOpen(false);
      toast.success('File created successfully');
    } catch (error) {
      toast.error(error.message || 'Failed to create file');
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteFile = async (fileId, fileName) => {
    if (window.confirm(`Are you sure you want to delete "${fileName}"?`)) {
      try {
        await onFileDelete(fileId);
        toast.success('File deleted successfully');
      } catch (error) {
        toast.error(error.message || 'Failed to delete file');
      }
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const getFileIcon = (filename) => {
    const extension = filename.split('.').pop()?.toLowerCase();
    const iconMap = {
      'js': 'üü®',
      'jsx': '‚öõÔ∏è',
      'ts': 'üî∑',
      'tsx': '‚öõÔ∏è',
      'py': 'üêç',
      'java': '‚òï',
      'cpp': 'üîß',
      'c': 'üîß',
      'css': 'üé®',
      'html': 'üåê',
      'json': 'üìã',
      'md': 'üìù',
      'sql': 'üóÉÔ∏è',
      'php': 'üêò',
      'rb': 'üíé',
      'go': 'üêπ',
      'rs': 'ü¶Ä',
      'swift': 'ü¶â',
      'kt': 'üÖ∫'
    };

    return iconMap[extension] || 'üìÑ';
  };

  return (
    <div className="w-80 bg-gray-800 border-r border-gray-700 flex flex-col">
      {/* Header */}
      <div className="p-4 border-b border-gray-700">
        <div className="flex items-center justify-between mb-3">
          <h2 className="text-lg font-semibold text-white">Files</h2>
          <div className="flex space-x-2">
            <button
              onClick={onRefresh}
              className="p-1 text-gray-400 hover:text-white transition-colors"
              title="Refresh files"
            >
              üîÑ
            </button>
            <button
              onClick={() => setIsCreateModalOpen(true)}
              className="px-3 py-1 bg-blue-600 text-white text-sm rounded hover:bg-blue-700 transition-colors"
            >
              + New
            </button>
          </div>
        </div>
        <div className="text-sm text-gray-400">
          {files.length} file{files.length !== 1 ? 's' : ''}
        </div>
      </div>

      {/* File List */}
      <div className="flex-1 overflow-y-auto">
        {files.length === 0 ? (
          <div className="p-4 text-center text-gray-400">
            <div className="text-4xl mb-2">üìÅ</div>
            <p>No files yet</p>
            <p className="text-xs mt-1">Create your first file to get started</p>
          </div>
        ) : (
          <div className="p-2">
            {files.map((file) => (
              <div
                key={file.id}
                className={`p-3 rounded-lg cursor-pointer transition-colors mb-2 group ${
                  selectedFileId === file.id
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-700 hover:bg-gray-600 text-gray-200'
                }`}
                onClick={() => onFileSelect(file)}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-2 flex-1 min-w-0">
                    <span className="text-lg">{getFileIcon(file.name)}</span>
                    <div className="flex-1 min-w-0">
                      <div className="font-medium truncate">{file.name}</div>
                      <div className="text-xs opacity-75">
                        {file.size} bytes ‚Ä¢ {formatDate(file.updatedAt)}
                      </div>
                    </div>
                  </div>

                  {file.ownerId === user?.id && (
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteFile(file.id, file.name);
                      }}
                      className="opacity-0 group-hover:opacity-100 p-1 text-red-400 hover:text-red-300 transition-all"
                      title="Delete file"
                    >
                      üóëÔ∏è
                    </button>
                  )}
                </div>

                {file.isShared && (
                  <div className="mt-1 text-xs opacity-75 flex items-center">
                    <span className="mr-1">üîó</span>
                    Shared file
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Create File Modal */}
      {isCreateModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-gray-800 rounded-lg p-6 w-96 max-w-full mx-4">
            <h3 className="text-lg font-semibold text-white mb-4">Create New File</h3>

            <form onSubmit={handleCreateFile}>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  File Name
                </label>
                <input
                  type="text"
                  value={newFileName}
                  onChange={(e) => setNewFileName(e.target.value)}
                  placeholder="e.g., index.js, style.css, README.md"
                  className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  autoFocus
                  disabled={isLoading}
                />
              </div>

              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Initial Content (optional)
                </label>
                <textarea
                  value={newFileContent}
                  onChange={(e) => setNewFileContent(e.target.value)}
                  placeholder="Enter initial file content..."
                  rows="4"
                  className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                  disabled={isLoading}
                />
              </div>

              <div className="flex space-x-3">
                <button
                  type="submit"
                  disabled={isLoading}
                  className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-gray-800 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {isLoading ? 'Creating...' : 'Create File'}
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setIsCreateModalOpen(false);
                    setNewFileName('');
                    setNewFileContent('');
                  }}
                  disabled={isLoading}
                  className="flex-1 px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2 focus:ring-offset-gray-800"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default FileExplorer;
