import React, { useRef, useEffect, useState } from 'react';
import Editor from '@monaco-editor/react';

const SimpleCodeEditor = ({ file, onContentChange, onAutoSave }) => {
  const editorRef = useRef(null);
  const [content, setContent] = useState(file?.content || '');
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const [lastSaved, setLastSaved] = useState(new Date());
  const autoSaveTimeoutRef = useRef(null);

  // Auto-save functionality
  useEffect(() => {
    if (hasUnsavedChanges && file?.id) {
      // Clear existing timeout
      if (autoSaveTimeoutRef.current) {
        clearTimeout(autoSaveTimeoutRef.current);
      }

      // Set new timeout for auto-save (5 seconds)
      autoSaveTimeoutRef.current = setTimeout(async () => {
        try {
          await onAutoSave(file.id, content);
          setHasUnsavedChanges(false);
          setLastSaved(new Date());
        } catch (error) {
          console.error('Auto-save failed:', error);
        }
      }, 5000);
    }

    return () => {
      if (autoSaveTimeoutRef.current) {
        clearTimeout(autoSaveTimeoutRef.current);
      }
    };
  }, [hasUnsavedChanges, content, file?.id, onAutoSave]);

  // Update content when file changes
  useEffect(() => {
    if (file?.content !== undefined) {
      setContent(file.content);
      setHasUnsavedChanges(false);
      setLastSaved(new Date());
    }
  }, [file?.id, file?.content]);

  const handleEditorDidMount = (editor, monaco) => {
    editorRef.current = editor;
    monaco.editor.setTheme('vs-dark');
  };

  const handleEditorChange = (value) => {
    setContent(value || '');
    setHasUnsavedChanges(true);
    onContentChange?.(value || '');
  };

  const getLanguageFromExtension = (filename) => {
    if (!filename) return 'javascript';

    const extension = filename.split('.').pop()?.toLowerCase();
    const languageMap = {
      'js': 'javascript',
      'jsx': 'javascript',
      'ts': 'typescript',
      'tsx': 'typescript',
      'py': 'python',
      'java': 'java',
      'cpp': 'cpp',
      'c': 'c',
      'css': 'css',
      'html': 'html',
      'json': 'json',
      'md': 'markdown',
    };

    return languageMap[extension] || 'plaintext';
  };

  if (!file) {
    return (
      <div style={{ 
        flex: 1, 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center', 
        backgroundColor: '#1e1e1e', 
        color: '#888' 
      }}>
        <div style={{ textAlign: 'center' }}>
          <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üìù</div>
          <h3 style={{ color: 'white', marginBottom: '0.5rem' }}>No file selected</h3>
          <p>Create a new file or select an existing one to start coding</p>
        </div>
      </div>
    );
  }

  return (
    <div style={{ flex: 1, display: 'flex', flexDirection: 'column', backgroundColor: '#1e1e1e' }}>
      {/* File header */}
      <div style={{ 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'space-between', 
        padding: '8px 16px', 
        backgroundColor: '#2d2d30', 
        borderBottom: '1px solid #3e3e42',
        color: 'white'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <span style={{ fontWeight: 500 }}>{file.name}</span>
          {hasUnsavedChanges && (
            <span style={{ 
              width: '8px', 
              height: '8px', 
              backgroundColor: '#ff9500', 
              borderRadius: '50%' 
            }} title="Unsaved changes"></span>
          )}
        </div>
        <div style={{ fontSize: '12px', color: '#888' }}>
          {hasUnsavedChanges ? 'Auto-saving...' : 'Saved'}
        </div>
      </div>

      {/* Editor */}
      <div style={{ flex: 1 }}>
        <Editor
          height="100%"
          language={getLanguageFromExtension(file.name)}
          value={content}
          theme="vs-dark"
          onChange={handleEditorChange}
          onMount={handleEditorDidMount}
          options={{
            fontSize: 14,
            minimap: { enabled: true },
            scrollBeyondLastLine: false,
            automaticLayout: true,
            tabSize: 2,
            wordWrap: 'on',
          }}
        />
      </div>
    </div>
  );
};

export default SimpleCodeEditor;
