import React, { useState } from 'react';
import { toast } from 'react-hot-toast';

const SimpleFileExplorer = ({ files, onFileSelect, onFileCreate, onFileDelete, onRefresh, selectedFileId }) => {
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [newFileName, setNewFileName] = useState('');
  const [newFileContent, setNewFileContent] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleCreateFile = async (e) => {
    e.preventDefault();

    if (!newFileName.trim()) {
      toast.error('Please enter a file name');
      return;
    }

    setIsLoading(true);
    try {
      await onFileCreate({
        name: newFileName.trim(),
        content: newFileContent
      });

      setNewFileName('');
      setNewFileContent('');
      setIsCreateModalOpen(false);
      toast.success('File created successfully');
    } catch (error) {
      toast.error(error.message || 'Failed to create file');
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteFile = async (fileId, fileName) => {
    if (window.confirm(`Are you sure you want to delete "${fileName}"?`)) {
      try {
        await onFileDelete(fileId);
        toast.success('File deleted successfully');
      } catch (error) {
        toast.error(error.message || 'Failed to delete file');
      }
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const getFileIcon = (filename) => {
    const extension = filename.split('.').pop()?.toLowerCase();
    const iconMap = {
      'js': '🟨', 'jsx': '⚛️', 'ts': '🔷', 'tsx': '⚛️',
      'py': '🐍', 'java': '☕', 'cpp': '🔧', 'c': '🔧',
      'css': '🎨', 'html': '🌐', 'json': '📋', 'md': '📝'
    };
    return iconMap[extension] || '📄';
  };

  return (
    <div style={{ 
      width: '320px', 
      backgroundColor: '#2d2d30', 
      borderRight: '1px solid #3e3e42', 
      display: 'flex', 
      flexDirection: 'column',
      color: 'white'
    }}>
      {/* Header */}
      <div style={{ padding: '16px', borderBottom: '1px solid #3e3e42' }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '12px' }}>
          <h2 style={{ fontSize: '18px', fontWeight: 600, margin: 0 }}>Files</h2>
          <div style={{ display: 'flex', gap: '8px' }}>
            <button
              onClick={onRefresh}
              style={{
                padding: '4px',
                backgroundColor: 'transparent',
                border: 'none',
                color: '#888',
                cursor: 'pointer',
                fontSize: '16px'
              }}
              title="Refresh files"
            >
              🔄
            </button>
            <button
              onClick={() => setIsCreateModalOpen(true)}
              style={{
                padding: '4px 12px',
                backgroundColor: '#0e639c',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '14px'
              }}
            >
              + New
            </button>
          </div>
        </div>
        <div style={{ fontSize: '14px', color: '#888' }}>
          {files.length} file{files.length !== 1 ? 's' : ''}
        </div>
      </div>

      {/* File List */}
      <div style={{ flex: 1, overflowY: 'auto' }}>
        {files.length === 0 ? (
          <div style={{ padding: '16px', textAlign: 'center', color: '#888' }}>
            <div style={{ fontSize: '2rem', marginBottom: '8px' }}>📁</div>
            <p>No files yet</p>
            <p style={{ fontSize: '12px', marginTop: '4px' }}>Create your first file to get started</p>
          </div>
        ) : (
          <div style={{ padding: '8px' }}>
            {files.map((file) => (
              <div
                key={file.id}
                style={{
                  padding: '12px',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  marginBottom: '8px',
                  backgroundColor: selectedFileId === file.id ? '#0e639c' : '#3c3c3c',
                  transition: 'background-color 0.2s',
                  position: 'relative'
                }}
                onClick={() => onFileSelect(file)}
                onMouseEnter={(e) => {
                  if (selectedFileId !== file.id) {
                    e.target.style.backgroundColor = '#4a4a4a';
                  }
                }}
                onMouseLeave={(e) => {
                  if (selectedFileId !== file.id) {
                    e.target.style.backgroundColor = '#3c3c3c';
                  }
                }}
              >
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px', flex: 1, minWidth: 0 }}>
                    <span style={{ fontSize: '18px' }}>{getFileIcon(file.name)}</span>
                    <div style={{ flex: 1, minWidth: 0 }}>
                      <div style={{ fontWeight: 500, overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>
                        {file.name}
                      </div>
                      <div style={{ fontSize: '12px', color: '#888' }}>
                        {file.size} bytes • {formatDate(file.updatedAt)}
                      </div>
                    </div>
                  </div>

                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDeleteFile(file.id, file.name);
                    }}
                    style={{
                      padding: '4px',
                      backgroundColor: 'transparent',
                      border: 'none',
                      color: '#ff6b6b',
                      cursor: 'pointer',
                      opacity: 0,
                      transition: 'opacity 0.2s'
                    }}
                    onMouseEnter={(e) => e.target.style.opacity = 1}
                    onMouseLeave={(e) => e.target.style.opacity = 0}
                    title="Delete file"
                  >
                    🗑️
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Create File Modal */}
      {isCreateModalOpen && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0,0,0,0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div style={{
            backgroundColor: '#2d2d30',
            borderRadius: '8px',
            padding: '24px',
            width: '400px',
            maxWidth: '90vw'
          }}>
            <h3 style={{ fontSize: '18px', fontWeight: 600, marginBottom: '16px', margin: '0 0 16px 0' }}>
              Create New File
            </h3>

            <form onSubmit={handleCreateFile}>
              <div style={{ marginBottom: '16px' }}>
                <label style={{ display: 'block', fontSize: '14px', fontWeight: 500, marginBottom: '8px' }}>
                  File Name
                </label>
                <input
                  type="text"
                  value={newFileName}
                  onChange={(e) => setNewFileName(e.target.value)}
                  placeholder="e.g., index.js, style.css, README.md"
                  style={{
                    width: '100%',
                    padding: '8px 12px',
                    backgroundColor: '#1e1e1e',
                    border: '1px solid #3e3e42',
                    borderRadius: '4px',
                    color: 'white',
                    fontSize: '14px',
                    boxSizing: 'border-box'
                  }}
                  autoFocus
                  disabled={isLoading}
                />
              </div>

              <div style={{ marginBottom: '24px' }}>
                <label style={{ display: 'block', fontSize: '14px', fontWeight: 500, marginBottom: '8px' }}>
                  Initial Content (optional)
                </label>
                <textarea
                  value={newFileContent}
                  onChange={(e) => setNewFileContent(e.target.value)}
                  placeholder="Enter initial file content..."
                  rows="4"
                  style={{
                    width: '100%',
                    padding: '8px 12px',
                    backgroundColor: '#1e1e1e',
                    border: '1px solid #3e3e42',
                    borderRadius: '4px',
                    color: 'white',
                    fontSize: '14px',
                    resize: 'none',
                    boxSizing: 'border-box'
                  }}
                  disabled={isLoading}
                />
              </div>

              <div style={{ display: 'flex', gap: '12px' }}>
                <button
                  type="submit"
                  disabled={isLoading}
                  style={{
                    flex: 1,
                    padding: '8px 16px',
                    backgroundColor: '#0e639c',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    fontSize: '14px',
                    opacity: isLoading ? 0.5 : 1
                  }}
                >
                  {isLoading ? 'Creating...' : 'Create File'}
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setIsCreateModalOpen(false);
                    setNewFileName('');
                    setNewFileContent('');
                  }}
                  disabled={isLoading}
                  style={{
                    flex: 1,
                    padding: '8px 16px',
                    backgroundColor: '#6c757d',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    fontSize: '14px'
                  }}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default SimpleFileExplorer;
