import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import FileExplorer from '../components/SimpleFileExplorer';
import CodeEditor from '../components/SimpleCodeEditor';
import { toast } from 'react-hot-toast';

const CodeWorkspace = () => {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const { user } = useAuth();

  const API_BASE_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5001';

  // Fetch files
  const fetchFiles = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_BASE_URL}/api/files`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setFiles(data.files || []);
      } else {
        const errorData = await response.json();
        toast.error(errorData.message || 'Failed to fetch files');
      }
    } catch (error) {
      console.error('Error fetching files:', error);
      toast.error('Network error while fetching files');
    } finally {
      setIsLoading(false);
    }
  }, [API_BASE_URL]);

  // Fetch a specific file with content
  const fetchFile = async (fileId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_BASE_URL}/api/files/${fileId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        return data.file;
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to fetch file');
      }
    } catch (error) {
      console.error('Error fetching file:', error);
      throw error;
    }
  };

  // Create new file
  const handleFileCreate = async (fileData) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_BASE_URL}/api/files`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(fileData)
      });

      if (response.ok) {
        const data = await response.json();
        setFiles(prevFiles => [data.file, ...prevFiles]);
        setSelectedFile(data.file);
        return data.file;
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to create file');
      }
    } catch (error) {
      console.error('Error creating file:', error);
      throw error;
    }
  };

  // Delete file
  const handleFileDelete = async (fileId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_BASE_URL}/api/files/${fileId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        setFiles(prevFiles => prevFiles.filter(file => file.id !== fileId));
        if (selectedFile?.id === fileId) {
          setSelectedFile(null);
        }
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to delete file');
      }
    } catch (error) {
      console.error('Error deleting file:', error);
      throw error;
    }
  };

  // Select file and load content
  const handleFileSelect = async (file) => {
    try {
      const fileWithContent = await fetchFile(file.id);
      setSelectedFile(fileWithContent);
    } catch (error) {
      toast.error(error.message || 'Failed to load file');
    }
  };

  // Auto-save file content
  const handleAutoSave = async (fileId, content) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_BASE_URL}/api/files/${fileId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ content })
      });

      if (response.ok) {
        const data = await response.json();
        // Update file in the list
        setFiles(prevFiles => 
          prevFiles.map(file => 
            file.id === fileId 
              ? { ...file, updatedAt: data.file.updatedAt, size: data.file.size }
              : file
          )
        );
        // Update selected file
        setSelectedFile(prevFile => 
          prevFile?.id === fileId 
            ? { ...prevFile, content, updatedAt: data.file.updatedAt, size: data.file.size }
            : prevFile
        );
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to save file');
      }
    } catch (error) {
      console.error('Error auto-saving file:', error);
      throw error;
    }
  };

  // Handle content change (real-time updates)
  const handleContentChange = (content) => {
    setSelectedFile(prevFile => 
      prevFile ? { ...prevFile, content } : null
    );
  };

  // Initial load
  useEffect(() => {
    fetchFiles();
  }, [fetchFiles]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-white text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
          <p>Loading workspace...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 flex">
      {/* File Explorer */}
      <FileExplorer
        files={files}
        selectedFileId={selectedFile?.id}
        onFileSelect={handleFileSelect}
        onFileCreate={handleFileCreate}
        onFileDelete={handleFileDelete}
        onRefresh={fetchFiles}
      />

      {/* Code Editor */}
      <CodeEditor
        file={selectedFile}
        onContentChange={handleContentChange}
        onAutoSave={handleAutoSave}
      />
    </div>
  );
};

export default CodeWorkspace;
