import React, { useRef, useEffect, useState } from 'react';
import Editor from '@monaco-editor/react';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-hot-toast';

const CodeEditor = ({ file, onContentChange, onAutoSave }) => {
  const editorRef = useRef(null);
  const [content, setContent] = useState(file?.content || '');
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const [lastSaved, setLastSaved] = useState(new Date());
  const autoSaveTimeoutRef = useRef(null);
  const { user } = useAuth();

  // Auto-save functionality
  useEffect(() => {
    if (hasUnsavedChanges && file?.id) {
      // Clear existing timeout
      if (autoSaveTimeoutRef.current) {
        clearTimeout(autoSaveTimeoutRef.current);
      }

      // Set new timeout for auto-save (5 seconds)
      autoSaveTimeoutRef.current = setTimeout(async () => {
        try {
          await onAutoSave(file.id, content);
          setHasUnsavedChanges(false);
          setLastSaved(new Date());
        } catch (error) {
          console.error('Auto-save failed:', error);
          // Don't show toast for auto-save failures to keep it silent
        }
      }, 5000);
    }

    return () => {
      if (autoSaveTimeoutRef.current) {
        clearTimeout(autoSaveTimeoutRef.current);
      }
    };
  }, [hasUnsavedChanges, content, file?.id, onAutoSave]);

  // Update content when file changes
  useEffect(() => {
    if (file?.content !== undefined) {
      setContent(file.content);
      setHasUnsavedChanges(false);
      setLastSaved(new Date());
    }
  }, [file?.id, file?.content]);

  const handleEditorDidMount = (editor, monaco) => {
    editorRef.current = editor;

    // Set editor theme
    monaco.editor.setTheme('vs-dark');

    // Configure editor options
    editor.updateOptions({
      fontSize: 14,
      lineHeight: 20,
      minimap: { enabled: true },
      scrollBeyondLastLine: false,
      automaticLayout: true,
      tabSize: 2,
      insertSpaces: true,
      wordWrap: 'on',
      renderWhitespace: 'selection'
    });
  };

  const handleEditorChange = (value) => {
    setContent(value || '');
    setHasUnsavedChanges(true);
    onContentChange?.(value || '');
  };

  const getLanguageFromExtension = (filename) => {
    if (!filename) return 'javascript';

    const extension = filename.split('.').pop()?.toLowerCase();
    const languageMap = {
      'js': 'javascript',
      'jsx': 'javascript',
      'ts': 'typescript',
      'tsx': 'typescript',
      'py': 'python',
      'java': 'java',
      'cpp': 'cpp',
      'c': 'c',
      'css': 'css',
      'html': 'html',
      'json': 'json',
      'md': 'markdown',
      'sql': 'sql',
      'php': 'php',
      'rb': 'ruby',
      'go': 'go',
      'rs': 'rust',
      'swift': 'swift',
      'kt': 'kotlin',
      'xml': 'xml',
      'yaml': 'yaml',
      'yml': 'yaml'
    };

    return languageMap[extension] || 'plaintext';
  };

  const formatLastSaved = (date) => {
    const now = new Date();
    const diffMs = now - date;
    const diffSecs = Math.floor(diffMs / 1000);
    const diffMins = Math.floor(diffSecs / 60);

    if (diffSecs < 60) {
      return 'Just now';
    } else if (diffMins < 60) {
      return `${diffMins} minute${diffMins === 1 ? '' : 's'} ago`;
    } else {
      return date.toLocaleTimeString();
    }
  };

  if (!file) {
    return (
      <div className="flex-1 flex items-center justify-center bg-gray-900 text-gray-400">
        <div className="text-center">
          <div className="text-6xl mb-4">📝</div>
          <h3 className="text-xl font-medium mb-2">No file selected</h3>
          <p>Create a new file or select an existing one to start coding</p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-1 flex flex-col bg-gray-900">
      {/* File header */}
      <div className="flex items-center justify-between px-4 py-2 bg-gray-800 border-b border-gray-700">
        <div className="flex items-center space-x-2">
          <span className="text-white font-medium">{file.name}</span>
          {hasUnsavedChanges && (
            <span className="w-2 h-2 bg-orange-400 rounded-full" title="Unsaved changes"></span>
          )}
        </div>
        <div className="text-xs text-gray-400">
          Last saved: {formatLastSaved(lastSaved)}
        </div>
      </div>

      {/* Editor */}
      <div className="flex-1">
        <Editor
          height="100%"
          language={getLanguageFromExtension(file.name)}
          value={content}
          theme="vs-dark"
          onChange={handleEditorChange}
          onMount={handleEditorDidMount}
          options={{
            fontSize: 14,
            lineHeight: 20,
            minimap: { enabled: true },
            scrollBeyondLastLine: false,
            automaticLayout: true,
            tabSize: 2,
            insertSpaces: true,
            wordWrap: 'on',
            renderWhitespace: 'selection',
            contextmenu: true,
            selectOnLineNumbers: true,
            roundedSelection: false,
            readOnly: false,
            cursorStyle: 'line',
            automaticLayout: true,
          }}
        />
      </div>

      {/* Status bar */}
      <div className="px-4 py-1 bg-gray-800 border-t border-gray-700 text-xs text-gray-400 flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <span>Language: {getLanguageFromExtension(file.name)}</span>
          <span>Size: {file.size || content.length} bytes</span>
        </div>
        <div className="flex items-center space-x-2">
          {hasUnsavedChanges ? (
            <span className="text-orange-400">Auto-saving...</span>
          ) : (
            <span className="text-green-400">Saved</span>
          )}
        </div>
      </div>
    </div>
  );
};

export default CodeEditor;
